import React, { useState } from 'react';
import './carousel.css'

function FieldsetCarousel({ props, estado, setEstado }) {



  const handlePrev = () => {
    setEstado(estado === 0 ? props.length - 1 : estado - 1);
  };

  const handleNext = () => {
    setEstado(estado === props.length - 1 ? 0 : estado + 1);
  };

  return (
    <div className="fieldset-carousel">
      <fieldset>
        <h2>{props[estado].title}</h2>
        <p><strong>{props[estado].importante}</strong></p>
        <p><i>{props[estado].aviso1}</i></p>
        <p>{props[estado].parrafo1}<input></input></p>
        <p><i>{props[estado].aviso2}</i></p>
        <p>{props[estado].parrafo2}</p>
        <p><i>{props[estado].aviso3}</i></p>
        <p>{props[estado].parrafo3}</p>
        <p><i>{props[estado].aviso4}</i></p>
        <p>{props[estado].parrafo4}</p>
        <p><i>{props[estado].aviso5}</i></p>
        <p>{props[estado].parrafo5}</p>
        <p><i>{props[estado].aviso6}</i></p>
        <p>{props[estado].parrafo6}</p>

      </fieldset>
      <div className="buttons">
        <img src={require('./img/vidapp.png')} onClick={handlePrev}/>
        <img src={require('./img/vidapp.png')} onClick={handleNext}/>
      </div>
    </div>
  );
};

export default FieldsetCarousel;
