import React, { useState } from 'react';
import './carousel.css'
import Modal from "./Modal";
import FontSizeButton from './FontSizeButton';
import { Route, Routes, useNavigate } from "react-router-dom";
import Home from './pages/Home';
import { List } from './list'


function FieldsetCarousel({ estado, setEstado }) {
  // const [resultado, setResultado] = useState(0);


  const request = async (data) => { 
    const rules = {
        input1: { min: 150},
        input2: { min: 150},
        input3: { min: 120},
        input4: { min: 100},
        input5: { min: 150},
        input6: { min: 80},
        input7: { min: 120},
        input8: { min: 120},
        input9: { min: 120},
        input10: { min: 5},
        input11: { min: 80},
        input12: { min: 15},
        input13: { min: 80, max: 110},
        input14: { min: 15},
    }


        const adapted =  Object.keys(data).reduce((acc, key) => {
        const { min, max } = rules[key]
        const value = data[key]
        if (value < min || value > max) {
            acc[key] = 'unadapted'
        } else {
            acc[key] = 'adapted'
        }
        return acc
    }, {})

    console.log("Adapted: " ,adapted);
    const rate = Object.values(adapted).filter((value) => value === 'adapted');
    console.log("Rate: ", rate);
    const result = Math.round((rate.length * 100)/14)
    // setResultado(Math.round((rate.length * 100)/14));
    // console.log("result: ", resultado)


  //   if(alert(`Su vivienda se encuentra adaptado en un ${result}%`)){}
  // else    window.location.reload(); 
  };


  const [form, setForm] = useState({
    input1: '',
    input2: '',
    input3: '',
    input4: '',
    input5: '',
    input6: '',
    input7: '',
    input8: '',
    input9: '',
    input10: '',
    input11: '',
    input12: '',
    input13: '',
    input14: ''
});

// const handleReset = () => {
//     setForm ({input1: '',
//     input2: '',
//     input3: '',
//     input4: '',
//     input5: '',
//     input6: '',
//     input7: '',
//     input8: '',
//     input9: '',
//     input10: '',
//     input11: '',
//     input12: '',
//     input13: '',
//     input14: ''})
// }
const navigate = useNavigate();

  const handlePrev = () => {
    setEstado(estado === 0 ? List.length - 1 : estado - 1);
  };

  const handleNext = () => {
    setEstado(estado === List.length - 1 ? 0 : estado + 1);
  };

  const log = () => console.log(form);
  return (

    <div className="fieldset-carousel">
      <header className='Header'>
                <img src={require('./img/vidapp.png')} alt="" />
                </header>
      {/* <form> */}
        <fieldset>
        { List[estado](form, setForm)}
        </fieldset>
      {/* </form> */}
      <div className="arrows">
        <img className='arrow' src={require('./img/formulario/prev.png')} onClick={handlePrev}/>
        <img className='arrow' src={require('./img/formulario/next.png')} onClick={handleNext}/>
      </div>
      <div className='submitDiv'>
        <button onClick={async () => {
                        const data = await request(form);
                        setForm(data);
                    }} className='button'>Calcular</button>
      </div>
      <a><img onClick={() => navigate("/")} className="home" src={require('./img/iconos/hogar.png')} alt="" /></a>
      <FontSizeButton/>


    </div>

  );
};



export default FieldsetCarousel;
