import React, { useState } from 'react';
import "./minus.css"

function NumberInput() {
  const [value, setValue] = useState('');

  const handleInputChange = event => {
    setValue(event.target.value);
  }

  const handleKeyDown = event => {
    if (event.keyCode === 40 || event.keyCode === 38 || event.keyCode === 189 || event.keyCode === 187 || event.keyCode === 69 || event.keyCode === 109 || event.keyCode === 109  ) {
      event.preventDefault();
    }
  }

  return (
    <input className="minus" type="number" value={value} onChange={handleInputChange} onKeyDown={handleKeyDown} />
  );
}

export default NumberInput;
