import React, { useState } from 'react';
import './carousel.css'

function FieldsetCarousel({ props }) {
  const [activeIndex, setActiveIndex] = useState(0);

  const { form } = props; 

  const handlePrev = () => {
    setActiveIndex(activeIndex === 0 ? props.length - 1 : activeIndex - 1);
  };

  const handleNext = () => {
    setActiveIndex(activeIndex === props.length - 1 ? 0 : activeIndex + 1);
  };

  return (
    <div>
      {form.map((item, index) => (
        <div key={index}>
          <p>Key 1: {item.key1}</p>
          <p>Key 2: {item.key2}</p>
        </div>
      ))}
    </div>
  );

