import React, { useState } from 'react';
import './carousel.css'

function FieldsetCarousel({ props, estado, setEstado }) {



  const handlePrev = () => {
    setEstado(estado === 0 ? props.length - 1 : estado - 1);
  };

  const handleNext = () => {
    setEstado(estado === props.length - 1 ? 0 : estado + 1);
  };

  return (
    <div className="fieldset-carousel">
      <fieldset>
        <h2>{props[estado].title}</h2>
        <p><strong>{props[estado].importante}</strong></p>
        <p><i>{props[estado].aviso1}</i></p>
        <p>{props[estado].parrafo1}</p>        <input></input>

        <p><i>{props[estado].aviso2}</i></p>
        <p>{props[estado].parrafo2}</p>
        <p><i>{props[estado].aviso2}</i></p>
        <p></p>
        <p></p>        
      </fieldset>
      <div className="buttons">
        <button onClick={handlePrev}>Prev</button>
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default FieldsetCarousel;
