import React, { useState } from 'react';
import './carousel.css'

function FieldsetCarousel({ props, estado, setEstado }) {

  const form = [
    {
      title: 'Item 1',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
    },
    {
      title: 'Item 2',
      description: 'Praesent auctor, est in bibendum faucibus, mauris elit ullamcorper elit.',
    },
    {
      title: 'Item 3',
      description: 'Nulla'
    }]

  const handlePrev = () => {
    setEstado(estado === 0 ? props.length - 1 : estado - 1);
  };

  const handleNext = () => {
    setEstado(estado === props.length - 1 ? 0 : estado + 1);
  };

  return (
    <div className="fieldset-carousel">
      <fieldset>
        <legend>{props[estado].title}</legend>
        <p>{props[estado].description}</p>
      </fieldset>
      <div className="buttons">
        <button onClick={handlePrev}>Prev</button>
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default FieldsetCarousel;
