import FieldsetCarousel from "./Carousel";
import  { useState } from 'react';
import React from "react";

const Paint = () => {
    const [activeIndex, setActiveIndex] = useState(0);
    const form = [
        {
          title: 'Vestíbulos',
          importante: 'Importante: El espacio libre deberá ser igual',
          parrafo: '1. Diametro de circunferencia libre no barrida por las puertas (cm)'

        },
        {
          title: 'Pasillos',
          importante: 'Importante: En lo referente al pasillo nos interesa prestar atención al ancho del mismo. Este puede ser de dos formas: misma anchura(anchura libre) a largo de todo el pasillo, o por el contrario, sufrir algún tipo estrechamiento en ciertos puntos (entrechamiento puntual). Por favor, según el caso, marque la casilla para habilitar el resto de campos.',
          aviso1: 'En caso de que la anchura sea la misma a lo largo de todo el pasillo...',
          parrafo: '2. Ancho libre (cm)',
          parrafo: '3. Ancho libre (cm)',
          aviso3: 'En caso de que exista un estrechamiento puntual (como por ejemplo un pilar)',
          parrafo: '3. Ancho libre (cm)',
          parrafo: '4. Anchura del estrechamiento (cm)', 
          parrafo: '5. Ancho libre en el punto del estrechamiento (cm)', 
          aviso6: 'En caso de que su pasillo tenga una longitud de 10 metros o más indique por favor su anchura (cm)', 
          parrafo6: '6. Anchura libre' 
        },
        {
          title: 'Huecos de paso',
          parrafo: '7. Anchura libre de paso de la puerta de entrada (cm)',
          parrafo: '8. Anchura libre de paso de la puerta de entrada (cm)',

        },
        {
            title: 'Puertas de accesos interiores',
            importante: 'Importante : En esta sección deberá tomar medidas de los espacios cercanos a la puertas que acceden a las diferentes habitaciones de su hogar',
            parrafo: '9. Marque esta casilla en caso de que las puertas interiores tengan un ángulo de 90º o superior)',
            parrafo: '10. Distancia entre el suelo y el pomo (cm)',
            aviso1: 'En caso de puertas acristaladas/transaparentes, exista señalización sobre las mismas',
            parrafo: '11. Anchura de la señalización',
            aviso2: 'En caso de puertas acristaladas/transaparentes, exista señalización sobre las mismas',
            parrafo: '12. Anchura libre de paso',
            parrafo: '13. Tiempo estimado para apertura/cierra (segundos)',
        },
        {
            title: 'Ventanas',
            importante: 'Importante: En este campo se hace refrencia a la altura a la que se encuentran en mecanismo de apertura de las ventanas de su hogar. Esta medida se toma desde el suelo hasta la manivela/pomo de la ventana.',
            parrafo: '14. Altura del mecanismo de apertura',

        }
    ]
      return (
        <div>
          <FieldsetCarousel props={form} estado={activeIndex} setEstado={setActiveIndex} />
        </div>
      );

    }

    export default Paint;
