import React, { useState } from 'react';
import './carousel.css'
import Modal from "./Modal";

function FieldsetCarousel({ props, estado, setEstado }) {

  const handleOpenModal1 = () => {
    setmodalOpen1(true);
  };
  const handleOpenModal2 = () => {
    setmodalOpen2(true);
  };const handleOpenModal3 = () => {
    setmodalOpen3(true);
  };const handleOpenModal4 = () => {
    setmodalOpen4(true);
  };const handleOpenModal5 = () => {
    setmodalOpen5(true);
  };const handleOpenModal6 = () => {
    setmodalOpen6(true);
  };const handleOpenModal7 = () => {
    setmodalOpen7(true);
  };


  const handlePrev = () => {
    setEstado(estado === 0 ? props.length - 1 : estado - 1);
  };

  const handleNext = () => {
    setEstado(estado === props.length - 1 ? 0 : estado + 1);
  };

  return (
    <div className="fieldset-carousel">
      <fieldset>
      <div>{props[estado]}</div>
      </fieldset>
      <div className="arrows">
        <img className='arrow' src={require('./img/formulario/prev.png')} onClick={handlePrev}/>
        <img className='arrow' src={require('./img/formulario/next.png')} onClick={handleNext}/>
      </div>
      <div>
        <button className='button'>Calcular</button>
      </div>
    </div>
  );
};

export default FieldsetCarousel;
