import React from "react";
import "./home.css";
import logo from "../img/vidapp.png";
import vida from "../img/footer/Logo-Proyecto-Vida.png";
import icon1 from "../img/iconos/personal.svg";
import icon2 from "../img/iconos/profesional.svg";
// import { useNavigate } from "react-router-dom";
import Modal from "../Modal";
import { useState } from "react";



function Home(){
    const [modalOpen1, setmodalOpen1] = useState(false);
    // const navigate = useNavigate();

    const handleOpenModal = () => {
        setmodalOpen1(true);
      };
    return (

    <div>
        <header className='Header'>
            <h1>Bienvenido a</h1>
            <img src={logo} alt="" />

        </header>
            <h3>
                Esta harrmienta sirve al usuario como herramienta de comprobación de los parametros de accesibilidad de su vivienda. 
                Es decir, ¿en qué medida se encuentra adaptado el hogar para que personas mayores o con discapacidad lo habiten de forma cómoda?
            </h3>
            <div className="flex-container">
                <button onClick={handleOpenModal} className="btn1">
                    <span>Uso profesional </span>
                    <img className="icon" src={icon1} alt="" />
                </button>
                <button onClick={handleOpenModal} className="btn2">
                    <span>Uso personal</span>
                    <img className="icon" src={icon2} alt="" />
                </button>
            </div>
            <footer className="pie-pagina">
                <div className="grupo1">
                    <div className="box">
                        <figure>
                            <a href="https://www.vidafp.uma.es/">
                                <img id="vida" src={vida} alt="vida"/>
                            </a>
                        </figure>
                    </div>
                    <div className="box">
                        <h4> <a href="https://www.vidafp.uma.es/" target="_blank" rel="noreferrer">MEJORANDO LA CALIDAD DE VIDA DE LAS PERSONAS MAYORES EN SUS HOGARES</a></h4>
                        <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Earum unde magni .</p>
                        <p>Lorem ipsum dolor, sit amet consectetur adipisicing elit. Eum .</p>
                    </div>
                    <div className="box">
                        <h4>SIGUENOS</h4>
                        <div className="red-social">
                            {/* <img></img>
                            <img></img>
                            <img></img>
                            <img></img> */}
                        </div>

                    </div>
                </div>
                <div className="grupo2">
                    <small>&copy; 2023<b> Proyecto vida</b> - Todos los derechos reservados.</small>
                </div>
            </footer>
        <Modal estado={modalOpen1} setEstado={setmodalOpen1}>
                <h3>Qué es </h3>
                <div className="modalImg"><img src={logo} alt="" /></div>
                <p>
                    Esta herramineta sirve al usuario como herramienta de comprobación de 
                    los parametros de accesibilidad de su vivienda. Es decir, ¿en qué medida 
                    se encuentra adaptado el hogar para que personas mayores o con discapacidad lo habiten de forma cómoda?
                </p>
        </Modal>
    </div>


    )
}

export default Home;
