    var inputElement = document.getElementById("myInput");

    const handleButtonClick = () => {
      useEffect(() => {
        const formElement = document.querySelector('.form');

        if (formElement) {
          formElement.style.fontSize = isLarge ? '35px' : '25px';
        }

      }, [isLarge]);

  }

  useEffect(() => {
    const formElement = document.querySelector('.form');

    if (formElement) {
      formElement.style.fontSize = isLarge ? '35px' : '25px';
    }

