import React, { useState } from 'react';
import './carousel.css'

function FieldsetCarousel({ props }) {

  const { form } = props; 

  const handlePrev = () => {
    setActiveIndex(activeIndex === 0 ? props.length - 1 : activeIndex - 1);
  };

  const handleNext = () => {
    setActiveIndex(activeIndex === props.length - 1 ? 0 : activeIndex + 1);
  };

  return (
    <div className="fieldset-carousel">
      <fieldset>
        <legend>{props[activeIndex].title}</legend>
        <p>{props[activeIndex].description}</p>
      </fieldset>
      <div className="buttons">
        <button onClick={handlePrev}>Prev</button>
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default FieldsetCarousel;
