import React, { useState } from 'react';
import './carousel.css'

function FieldsetCarousel({ props, estado, setEstado }) {



  const handlePrev = () => {
    setEstado(estado === 0 ? props.length - 1 : estado - 1);
  };

  const handleNext = () => {
    setEstado(estado === props.length - 1 ? 0 : estado + 1);
  };

  const Item = () => (
    <div className='container'>
    <h2>{props[estado].title}</h2>
    <div className='container'>
    <p className='aviso'><i>{props[estado].aviso}</i></p>
    <p className='pregunta'>{props[estado].parrafo}<input></input></p>
    </div>
    </div>
  )

  const form = (props) => {
    return Object.keys(props[estado]).forEach((key) => {
        //switch para los diferentes tipos de inputs que se pueden usar en el formulario
        switch (key) {
            case 'title': {
                console.log('title')
                return <div className='container'><h2>{props[estado].title}</h2></div>
            }
                return <div className='container'><h2>{props[estado].title}</h2></div>
            case 'importante':
                return <p className='imprtante'><strong>{props[estado].importante}</strong></p>
            case 'aviso':
                return <p className='aviso'><i>{props[estado].aviso}</i></p>
            case 'parrafo':
                return <p className='pregunta'>{props[estado].parrafo}<input></input></p>
        }
    });
    };  

  return (
    <div className="fieldset-carousel">
      <fieldset>
<div className='container'>

        </div>
       { form(props) }
      </fieldset>
      <div className="buttons">
        <img className='arrow' src={require('./img/formulario/prev.png')} onClick={handlePrev}/>
        <img className='arrow' src={require('./img/formulario/next.png')} onClick={handleNext}/>
      </div>
    </div>
  );
};

export default FieldsetCarousel;
